import { z } from "zod";
import { configSchema, server } from "@/lib/config/schema";

export const loggingConfig = configSchema("Logging", {
  level: server({
    env: "LOG_LEVEL",
    schema: z
      .enum(["trace", "debug", "info", "warn", "error", "fatal"])
      .default("info"),
  }),
});

import pino from "pino";
import { mainConfig } from "@/lib/config/main";
import { loggingConfig } from "./config";

const isDev = mainConfig.server.nodeEnv === "development";

const transport = isDev
  ? pino.transport({
      target: "pino-pretty",
    })
  : undefined;

export const logger = pino(
  {
    level: loggingConfig.server.level,
  },
  transport,
);
